$(document).ready(function(){$(".board-slick").slick({prevArrow:$("#board-prev"),nextArrow:$("#board-next"),slidesToShow:1,infinite:!1});$(".board-slick").on("afterChange",function(a,b,c){$("#board-next").css("color","white");$("#board-prev").css("color","white");$(".slick-members:first-child").hasClass("slick-active")&&$("#board-prev").css("color","rgba(255,255,255,0.3)");$(".slick-members:last-child").hasClass("slick-active")&&$("#board-next").css("color","rgba(255,255,255,0.3)")})});